mapper.update:
update timer_task
task_name = #{taskName,jdbcType=VARCHAR},
task_group = #{taskGroup,jdbcType=VARCHAR},
config = #{config,jdbcType=VARCHAR}, closed = #{closed,jdbcType=TINYINT}, other_params = #{otherParams,jdbcType=VARCHAR}, policy_id = #{policyId,jdbcType=BIGINT}, description = #{description,jdbcType=VARCHAR}, create_by = #{createBy,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, last_update_by = #{lastUpdateBy,jdbcType=VARCHAR}, last_update_time = CURRENT_TIMESTAMP(), remarks = #{remarks,jdbcType=VARCHAR}, del_flag = #{delFlag,jdbcType=TINYINT}
where id = #{id,jdbcType=BIGINT}

